import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Upload, FileText, Camera, Download, CheckCircle, AlertTriangle, ChevronRight, Brain, X, AlertCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Checkbox } from "@/components/ui/checkbox";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useDropzone } from "react-dropzone";

interface UploadedFile {
  id: string;
  name: string;
  size: number;
  type: string;
  url: string;
  uploadedAt: Date;
  category: string;
  description?: string;
}

interface EvidenceCategory {
  id: string;
  name: string;
  description: string;
  required: boolean;
  acceptedTypes: string[];
  maxFiles: number;
  files: UploadedFile[];
  priority: "Critical" | "High" | "Medium" | "Low";
  isUnavailable?: boolean;
  unavailableReason?: string;
}

interface Incident {
  id: number;
  title: string;
  equipmentGroup: string;
  equipmentType: string;
  equipmentId: string;
  currentStep: number;
  workflowStatus: string;
  evidenceChecklist?: any[];
}

export default function EvidenceCollection() {
  const [, setLocation] = useLocation();
  const [incidentId, setIncidentId] = useState<number | null>(null);
  const [evidenceCategories, setEvidenceCategories] = useState<EvidenceCategory[]>([]);
  const [activeCategory, setActiveCategory] = useState<string>("");
  const [isUploading, setIsUploading] = useState(false);
  const [completionPercentage, setCompletionPercentage] = useState(0);

  // Extract incident ID from URL parameters
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const id = params.get('incident');
    if (id) {
      setIncidentId(parseInt(id));
    }
  }, []);

  // Fetch incident details
  const { data: incident, isLoading } = useQuery({
    queryKey: ['/api/incidents', incidentId],
    enabled: !!incidentId,
  });

  // Generate evidence collection categories
  const generateCategoriesMutation = useMutation({
    mutationFn: async (incidentData: Incident) => {
      const response = await fetch(`/api/incidents/${incidentData.id}/generate-evidence-categories`, {
        method: 'POST',
        body: JSON.stringify({
          equipmentGroup: incidentData.equipmentGroup,
          equipmentType: incidentData.equipmentType,
          evidenceChecklist: incidentData.evidenceChecklist,
        }),
        headers: { 'Content-Type': 'application/json' },
      });

      if (!response.ok) {
        throw new Error(`Failed to generate categories: ${response.status}`);
      }

      return await response.json();
    },
    onSuccess: (data) => {
      console.log('Evidence categories generated:', data);
      if (data && data.categories && Array.isArray(data.categories)) {
        setEvidenceCategories(data.categories);
        if (data.categories.length > 0) {
          setActiveCategory(data.categories[0].id);
        }
      } else {
        console.error('Invalid evidence categories format:', data);
        setEvidenceCategories([]);
      }
    },
    onError: (error) => {
      console.error('Failed to generate evidence categories:', error);
    },
  });

  // File upload mutation
  const uploadFileMutation = useMutation({
    mutationFn: async (data: { file: File; categoryId: string; description?: string }) => {
      const formData = new FormData();
      formData.append('file', data.file);
      formData.append('categoryId', data.categoryId);
      formData.append('incidentId', incidentId!.toString());
      if (data.description) {
        formData.append('description', data.description);
      }

      return fetch(`/api/incidents/${incidentId}/upload-evidence`, {
        method: 'POST',
        body: formData,
      }).then(res => res.json());
    },
    onSuccess: (data, variables) => {
      // Update the category with the new file
      setEvidenceCategories(prev => 
        prev.map(category => 
          category.id === variables.categoryId 
            ? { ...category, files: [...category.files, data.file] }
            : category
        )
      );
      setIsUploading(false);
    },
  });

  // Generate categories when incident loads
  useEffect(() => {
    if (incident && Array.isArray(evidenceCategories) && evidenceCategories.length === 0) {
      generateCategoriesMutation.mutate(incident);
    }
  }, [incident]);

  // Calculate completion percentage - includes evidence marked as unavailable with reasons
  useEffect(() => {
    if (Array.isArray(evidenceCategories) && evidenceCategories.length > 0) {
      const requiredCategories = evidenceCategories.filter(cat => cat.required);
      const completedRequired = requiredCategories.filter(cat => 
        cat.files.length > 0 || (cat.isUnavailable && cat.unavailableReason?.trim())
      );
      const optionalCategories = evidenceCategories.filter(cat => !cat.required);
      const completedOptional = optionalCategories.filter(cat => 
        cat.files.length > 0 || (cat.isUnavailable && cat.unavailableReason?.trim())
      );

      // 70% weight for required, 30% for optional
      const requiredScore = requiredCategories.length > 0 ? (completedRequired.length / requiredCategories.length) * 70 : 70;
      const optionalScore = optionalCategories.length > 0 ? (completedOptional.length / optionalCategories.length) * 30 : 30;

      setCompletionPercentage(Math.round(requiredScore + optionalScore));
    }
  }, [evidenceCategories]);

  const handleFileUpload = (files: File[], categoryId: string, description?: string) => {
    files.forEach(file => {
      setIsUploading(true);
      uploadFileMutation.mutate({ file, categoryId, description });
    });
  };

  const handleRemoveFile = (categoryId: string, fileId: string) => {
    setEvidenceCategories(prev => 
      prev.map(category => 
        category.id === categoryId 
          ? { ...category, files: category.files.filter(f => f.id !== fileId) }
          : category
      )
    );
  };

  const handleUnavailabilityChange = (categoryId: string, isUnavailable: boolean, reason?: string) => {
    setEvidenceCategories(prev => 
      prev.map(cat => 
        cat.id === categoryId 
          ? { 
              ...cat, 
              isUnavailable, 
              unavailableReason: reason || '',
              // Clear files if marking as unavailable
              files: isUnavailable ? [] : cat.files
            }
          : cat
      )
    );
  };

  // Updated logic: Allow progression if files uploaded OR evidence marked unavailable with reason
  const canProceed = evidenceCategories.filter(cat => cat.required).every(cat => 
    cat.files.length > 0 || (cat.isUnavailable && cat.unavailableReason?.trim())
  );

  const handleProceedToAnalysis = () => {
    if (incidentId) {
      setLocation(`/ai-analysis?incident=${incidentId}`);
    }
  };

  if (isLoading || !incident) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <Brain className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Loading incident details...</p>
        </div>
      </div>
    );
  }

  const activeCategoryData = evidenceCategories.find(cat => cat.id === activeCategory);

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="bg-card shadow-sm border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <Button 
                variant="ghost" 
                onClick={() => setLocation('/')}
              >
                ‚Üê Back to Home
              </Button>
              <div className="flex items-center space-x-2">
                <img 
                  src="/quanntaum-logo.jpg" 
                  alt="Quanntaum Logo" 
                  className="h-5 w-5 rounded object-contain"
                />
                <h1 className="text-xl font-bold">Step 4: Evidence Collection</h1>
              </div>
            </div>
            <Badge variant="outline" className="text-sm">
              Incident #{incident.id}
            </Badge>
          </div>
        </div>
      </header>

      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Progress Overview */}
        <Card className="mb-8">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="flex items-center gap-2">
                  <FileText className="h-5 w-5" />
                  {incident.title}
                </CardTitle>
                <CardDescription>
                  Equipment: {incident.equipmentGroup} ‚Üí {incident.equipmentType} ({incident.equipmentId})
                </CardDescription>
              </div>
              <div className="text-right">
                <div className="text-2xl font-bold text-primary">{completionPercentage}%</div>
                <div className="text-sm text-muted-foreground">Evidence Collected</div>
              </div>
            </div>
            <Progress value={completionPercentage} className="mt-4" />
          </CardHeader>
        </Card>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Evidence Categories Sidebar */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle className="text-base">Evidence Categories</CardTitle>
                <CardDescription>
                  {evidenceCategories.filter(cat => cat.required).length} required, {evidenceCategories.filter(cat => !cat.required).length} optional
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-2">
                {evidenceCategories.map((category) => (
                  <button
                    key={category.id}
                    onClick={() => setActiveCategory(category.id)}
                    className={`w-full text-left p-3 rounded-lg border transition-colors ${
                      activeCategory === category.id 
                        ? 'border-primary bg-primary/5' 
                        : 'border-border hover:border-primary/50'
                    } ${category.isUnavailable ? 'bg-orange-50 border-orange-200' : ''}`}
                  >
                    <div className="flex items-center justify-between mb-1">
                      <span className="font-medium text-sm">{category.name}</span>
                      {category.isUnavailable ? (
                        <AlertCircle className="h-4 w-4 text-orange-600" />
                      ) : category.files.length > 0 ? (
                        <CheckCircle className="h-4 w-4 text-green-600" />
                      ) : category.required ? (
                        <AlertTriangle className="h-4 w-4 text-orange-500" />
                      ) : (
                        <div className="h-4 w-4" />
                      )}
                    </div>
                    <div className="flex items-center gap-2">
                      <Badge 
                        variant={category.required ? "destructive" : "secondary"}
                        className="text-xs"
                      >
                        {category.required ? "Required" : "Optional"}
                      </Badge>
                      {category.isUnavailable && (
                        <Badge variant="outline" className="text-xs bg-orange-100 border-orange-300">
                          Not Available
                        </Badge>
                      )}
                      <span className="text-xs text-muted-foreground">
                        {category.isUnavailable 
                          ? "Documented" 
                          : `${category.files.length}/${category.maxFiles} files`
                        }
                      </span>
                    </div>
                  </button>
                ))}
              </CardContent>
            </Card>
          </div>

          {/* Evidence Upload Area */}
          <div className="lg:col-span-3">
            {activeCategoryData && (
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <div>
                      <CardTitle className="flex items-center gap-2">
                        {activeCategoryData.name}
                        <Badge variant={activeCategoryData.required ? "destructive" : "secondary"}>
                          {activeCategoryData.required ? "Required" : "Optional"}
                        </Badge>
                        {activeCategoryData.isUnavailable && (
                          <Badge variant="outline" className="bg-orange-100 border-orange-300 text-orange-800">
                            Not Available
                          </Badge>
                        )}
                      </CardTitle>
                      <CardDescription>
                        {activeCategoryData.description}
                      </CardDescription>
                    </div>
                    <div className="text-sm text-muted-foreground">
                      {activeCategoryData.isUnavailable 
                        ? "Evidence documented as unavailable" 
                        : `${activeCategoryData.files.length} / ${activeCategoryData.maxFiles} files`
                      }
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <EvidenceUploadZone 
                    category={activeCategoryData}
                    onFileUpload={handleFileUpload}
                    onRemoveFile={handleRemoveFile}
                    isUploading={isUploading}
                    onUnavailabilityChange={handleUnavailabilityChange}
                  />
                </CardContent>
              </Card>
            )}
          </div>
        </div>

        {/* Action Buttons */}
        <div className="mt-8 flex justify-between">
          <Button 
            variant="outline" 
            onClick={() => setLocation(`/evidence-checklist?incident=${incidentId}`)}
          >
            ‚Üê Back to Evidence Checklist
          </Button>
          <Button 
            onClick={handleProceedToAnalysis}
            disabled={!canProceed}
            className="flex items-center gap-2"
          >
            Proceed to AI Analysis
            <ChevronRight className="h-4 w-4" />
          </Button>
        </div>

        {/* Requirements Alert */}
        {!canProceed && evidenceCategories.length > 0 && (
          <Alert className="mt-4 border-amber-200 bg-amber-50">
            <AlertTriangle className="h-4 w-4" />
            <AlertDescription>
              <strong>Requirements:</strong> For each required evidence category, either upload files OR mark as unavailable with explanation.
            </AlertDescription>
          </Alert>
        )}
      </div>
    </div>
  );
}

function EvidenceUploadZone({ 
  category, 
  onFileUpload, 
  onRemoveFile, 
  isUploading,
  onUnavailabilityChange
}: { 
  category: EvidenceCategory;
  onFileUpload: (files: File[], categoryId: string, description?: string) => void;
  onRemoveFile: (categoryId: string, fileId: string) => void;
  isUploading: boolean;
  onUnavailabilityChange: (categoryId: string, isUnavailable: boolean, reason?: string) => void;
}) {
  const [fileDescription, setFileDescription] = useState("");
  const [unavailableReason, setUnavailableReason] = useState(category.unavailableReason || "");

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop: (acceptedFiles) => {
      if (acceptedFiles.length > 0) {
        onFileUpload(acceptedFiles, category.id, fileDescription);
        setFileDescription("");
      }
    },
    accept: category.acceptedTypes.reduce((acc, type) => ({
      ...acc,
      [type]: []
    }), {}),
    maxFiles: category.maxFiles - category.files.length,
    disabled: category.files.length >= category.maxFiles || isUploading,
  });

  return (
    <div className="space-y-4">
      {/* Evidence Not Available Option */}
      <div className="border rounded-lg p-4 bg-orange-50 border-orange-200">
        <div className="flex items-start space-x-3">
          <Checkbox
            id={`unavailable-${category.id}`}
            checked={category.isUnavailable || false}
            onCheckedChange={(checked) => {
              onUnavailabilityChange(category.id, checked as boolean, unavailableReason);
            }}
          />
          <div className="flex-1">
            <Label 
              htmlFor={`unavailable-${category.id}`} 
              className="text-sm font-medium cursor-pointer flex items-center gap-2"
            >
              <AlertCircle className="h-4 w-4 text-orange-600" />
              This evidence is not available or accessible
            </Label>
            <p className="text-xs text-muted-foreground mt-1">
              Check this if you cannot access this type of evidence due to system limitations, time constraints, or data availability
            </p>

            {category.isUnavailable && (
              <div className="mt-3">
                <Label htmlFor={`reason-${category.id}`} className="text-sm font-medium">
                  Why is this evidence unavailable? *
                </Label>
                <Textarea
                  id={`reason-${category.id}`}
                  placeholder="e.g., 'DCS system not recording vibration data', 'No maintenance logs available for this equipment', 'System shutdown - no trending data captured'..."
                  value={unavailableReason}
                  onChange={(e) => {
                    setUnavailableReason(e.target.value);
                    onUnavailabilityChange(category.id, true, e.target.value);
                  }}
                  className="mt-1"
                  rows={2}
                  required
                />
              </div>
            )}
          </div>
        </div>
      </div>

      {/* File Upload Area - Only show if evidence is available */}
      {!category.isUnavailable && category.files.length < category.maxFiles && (
        <div
          {...getRootProps()}
          className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors cursor-pointer ${
            isDragActive 
              ? 'border-primary bg-primary/5' 
              : 'border-border hover:border-primary/50'
          } ${isUploading ? 'opacity-50 cursor-not-allowed' : ''}`}
        >
          <input {...getInputProps()} />
          <Upload className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
          <div className="space-y-2">
            <p className="text-lg font-medium">
              {isDragActive ? 'Drop files here' : 'Drag files here or click to browse'}
            </p>
            <p className="text-sm text-muted-foreground">
              Accepted: {category.acceptedTypes.join(', ')}
            </p>
            <p className="text-xs text-muted-foreground">
              Max {category.maxFiles} files, {category.files.length} uploaded
            </p>
          </div>
        </div>
      )}

      {/* File Description - Only show if evidence is available */}
      {!category.isUnavailable && category.files.length < category.maxFiles && (
        <div>
          <Label htmlFor="file-description" className="text-sm font-medium">
            Optional Description
          </Label>
          <Textarea
            id="file-description"
            placeholder="Describe what this evidence shows..."
            value={fileDescription}
            onChange={(e) => setFileDescription(e.target.value)}
            className="mt-1"
            rows={2}
          />
        </div>
      )}

      {/* Uploaded Files - Show even if marked unavailable to allow user to manage */}
      {category.files.length > 0 && (
        <div className="space-y-3">
          <h4 className="font-medium text-sm flex items-center gap-2">
            Uploaded Files
            {category.isUnavailable && (
              <Badge variant="outline" className="text-xs bg-amber-100 border-amber-300">
                Files uploaded but evidence marked unavailable
              </Badge>
            )}
          </h4>
          {category.files.map((file) => (
            <div key={file.id} className={`flex items-center justify-between p-3 border rounded-lg ${
              category.isUnavailable ? 'bg-amber-50 border-amber-200' : 'bg-green-50 border-green-200'
            }`}>
              <div className="flex items-center gap-3">
                <FileText className={`h-5 w-5 ${category.isUnavailable ? 'text-amber-600' : 'text-green-600'}`} />
                <div>
                  <p className="font-medium text-sm">{file.name}</p>
                  <p className="text-xs text-muted-foreground">
                    {(file.size / 1024 / 1024).toFixed(2)} MB ‚Ä¢ {file.type}
                  </p>
                  {file.description && (
                    <p className="text-xs text-muted-foreground mt-1">{file.description}</p>
                  )}
                </div>
              </div>
              <div className="flex items-center gap-2">
                <Button variant="outline" size="sm" asChild>
                  <a href={file.url} download>
                    <Download className="h-4 w-4" />
                  </a>
                </Button>
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={() => onRemoveFile(category.id, file.id)}
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
            </div>
          ))}
          {category.isUnavailable && (
            <div className="text-xs text-amber-700 bg-amber-50 p-2 rounded border border-amber-200">
              <strong>Note:</strong> Evidence is marked as unavailable but files are still uploaded. 
              You can remove files or uncheck "not available" to use uploaded evidence.
            </div>
          )}
        </div>
      )}

      {/* Unavailable Evidence Summary */}
      {category.isUnavailable && category.unavailableReason && (
        <div className="bg-orange-50 border border-orange-200 rounded-lg p-4">
          <h4 className="font-medium text-sm text-orange-800 mb-2 flex items-center gap-2">
            <AlertCircle className="h-4 w-4" />
            Evidence Unavailability Documentation
          </h4>
          <p className="text-sm text-orange-700 mb-2">
            <strong>Reason:</strong> {category.unavailableReason}
          </p>
          <p className="text-xs text-orange-600">
            This documentation will be included in the final analysis to explain evidence limitations.
          </p>
        </div>
      )}

      {/* Category Full Message */}
      {category.files.length >= category.maxFiles && (
        <Alert className="border-green-200 bg-green-50">
          <CheckCircle className="h-4 w-4" />
          <AlertDescription>
            This category is complete! You've uploaded the maximum number of files ({category.maxFiles}).
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
}

client/src/components/ai-evidence-valida.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { 
  AlertCircle, 
  CheckCircle, 
  Brain, 
  MessageSquare, 
  HelpCircle,
  FileText,
  Target,
  Lightbulb
} from "lucide-react";

interface AIEvidenceValidatorProps {
  evidenceData: any;
  questionnaire: any[];
  onValidationUpdate: (validation: EvidenceValidation) => void;
  onPromptResponse: (fieldId: string, response: string) => void;
}

interface EvidenceValidation {
  isComplete: boolean;
  completeness: number;
  missingCritical: string[];
  flaggedInconsistent: string[];
  aiPrompts: AIPrompt[];
  readyForAnalysis: boolean;
}

interface AIPrompt {
  id: string;
  fieldId: string;
  message: string;
  severity: 'error' | 'warning' | 'suggestion';
  context: string;
  suggestedAction: string;
}

export default function AIEvidenceValidator({ 
  evidenceData, 
  questionnaire, 
  onValidationUpdate,
  onPromptResponse 
}: AIEvidenceValidatorProps) {
  const [validation, setValidation] = useState<EvidenceValidation>({
    isComplete: false,
    completeness: 0,
    missingCritical: [],
    flaggedInconsistent: [],
    aiPrompts: [],
    readyForAnalysis: false
  });

  const [activePrompts, setActivePrompts] = useState<AIPrompt[]>([]);
  const [showGuidance, setShowGuidance] = useState(false);

  useEffect(() => {
    performAIValidation();
  }, [evidenceData, questionnaire]);

  const performAIValidation = () => {
    const prompts: AIPrompt[] = [];
    const missingCritical: string[] = [];
    const flaggedInconsistent: string[] = [];

    // Critical field validation
    const criticalFields = questionnaire.filter(q => q.required);
    const totalFields = questionnaire.length;
    let completedFields = 0;

    questionnaire.forEach(question => {
      const value = evidenceData[question.id];
      const hasValue = value !== undefined && value !== null && value !== '';

      if (hasValue) {
        completedFields++;

        // AI validation of content quality
        validateFieldContent(question, value, prompts);
      } else if (question.required) {
        missingCritical.push(question.id);
        prompts.push({
          id: `missing_${question.id}`,
          fieldId: question.id,
          message: `Missing critical information: ${question.question}`,
          severity: 'error',
          context: 'This field is required for proper root cause analysis',
          suggestedAction: `Please provide ${question.question.toLowerCase()}`
        });
      }
    });

    // Equipment-specific validation
    performEquipmentValidation(evidenceData, prompts);

    // Cross-field consistency checks
    performConsistencyChecks(evidenceData, prompts, flaggedInconsistent);

    const completeness = Math.round((completedFields / totalFields) * 100);
    const readyForAnalysis = completeness >= 80 && missingCritical.length === 0;

    const newValidation: EvidenceValidation = {
      isComplete: completeness === 100,
      completeness,
      missingCritical,
      flaggedInconsistent,
      aiPrompts: prompts,
      readyForAnalysis
    };

    setValidation(newValidation);
    setActivePrompts(prompts.filter(p => p.severity === 'error' || p.severity === 'warning'));
    onValidationUpdate(newValidation);
  };

  const validateFieldContent = (question: any, value: any, prompts: AIPrompt[]) => {
    // Validate specific field types
    switch (question.id) {
      case 'observed_problem':
        if (typeof value === 'string' && value.length < 10) {
          prompts.push({
            id: `vague_${question.id}`,
            fieldId: question.id,
            message: 'Problem description seems too brief. Can you provide more details?',
            severity: 'warning',
            context: 'Detailed problem descriptions help identify root causes more accurately',
            suggestedAction: 'Describe symptoms, timing, severity, and any unusual observations'
          });
        }
        break;

      case 'equipment_tag':
        if (typeof value === 'string' && (value.length < 3 || !/[A-Z0-9]/.test(value))) {
          prompts.push({
            id: `invalid_tag_${question.id}`,
            fieldId: question.id,
            message: 'Equipment tag format looks unusual. Please verify the tag number.',
            severity: 'warning',
            context: 'Correct equipment identification is crucial for accurate analysis',
            suggestedAction: 'Use the official equipment tag from nameplate or P&ID'
          });
        }
        break;

      case 'event_datetime':
        const eventDate = new Date(value);
        const now = new Date();
        const daysDiff = (now.getTime() - eventDate.getTime()) / (1000 * 3600 * 24);

        if (daysDiff > 30) {
          prompts.push({
            id: `old_event_${question.id}`,
            fieldId: question.id,
            message: 'Event occurred more than 30 days ago. Evidence may be less reliable.',
            severity: 'suggestion',
            context: 'Fresh evidence provides more accurate analysis',
            suggestedAction: 'Consider if additional evidence is available from the time period'
          });
        }
        break;
    }
  };

  const performEquipmentValidation = (data: any, prompts: AIPrompt[]) => {
    const equipmentType = data.equipment_type;

    if (equipmentType === 'Centrifugal Pump') {
      // Pump-specific validation
      if (!data.operating_mode) {
        prompts.push({
          id: 'pump_operating_mode',
          fieldId: 'operating_mode',
          message: 'For pump failures, operating mode is critical. Was the pump running, starting, or stopped?',
          severity: 'error',
          context: 'Pump failure modes vary significantly based on operating state',
          suggestedAction: 'Specify if pump was running, starting up, shutting down, or idle'
        });
      }

      if (data.observed_problem?.toLowerCase().includes('seal') && !data.last_maintenance_date) {
        prompts.push({
          id: 'seal_maintenance',
          fieldId: 'last_maintenance_date',
          message: 'Seal failures often relate to maintenance history. When was the seal last serviced?',
          severity: 'warning',
          context: 'Pump seal life is directly related to maintenance intervals',
          suggestedAction: 'Provide last seal maintenance or replacement date if available'
        });
      }
    }
  };

  const performConsistencyChecks = (data: any, prompts: AIPrompt[], flagged: string[]) => {
    // Check for logical inconsistencies
    if (data.operator_error === true && data.procedures_followed === true) {
      flagged.push('operator_error', 'procedures_followed');
      prompts.push({
        id: 'inconsistent_operator',
        fieldId: 'operator_error',
        message: 'Inconsistency detected: Both "operator error" and "procedures followed" are marked as true.',
        severity: 'warning',
        context: 'These responses seem contradictory',
        suggestedAction: 'Please clarify if procedures were actually followed correctly'
      });
    }

    if (data.recent_changes === 'NO' && data.recent_work_details && data.recent_work_details !== 'NONE') {
      flagged.push('recent_changes', 'recent_work_details');
      prompts.push({
        id: 'inconsistent_changes',
        fieldId: 'recent_changes',
        message: 'Inconsistency: No recent changes reported, but work details provided.',
        severity: 'warning',
        context: 'Recent work might be relevant to the failure',
        suggestedAction: 'Clarify if recent work details should be considered as changes'
      });
    }
  };

  const dismissPrompt = (promptId: string) => {
    setActivePrompts(prev => prev.filter(p => p.id !== promptId));
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'error': return <AlertCircle className="h-4 w-4 text-red-500" />;
      case 'warning': return <AlertCircle className="h-4 w-4 text-amber-500" />;
      case 'suggestion': return <Lightbulb className="h-4 w-4 text-blue-500" />;
      default: return <HelpCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'error': return 'border-red-200 bg-red-50';
      case 'warning': return 'border-amber-200 bg-amber-50';
      case 'suggestion': return 'border-blue-200 bg-blue-50';
      default: return 'border-gray-200 bg-gray-50';
    }
  };

  return (
    <div className="space-y-4">
      {/* AI Validation Status */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Brain className="h-5 w-5 text-blue-600" />
            AI Evidence Validation
            <Badge variant={validation.readyForAnalysis ? "default" : "secondary"}>
              {validation.completeness}% Complete
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <Progress value={validation.completeness} className="mb-4" />

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div className="text-center p-3 bg-gray-50 rounded-lg">
              <div className="text-2xl font-bold text-gray-900">{validation.completeness}%</div>
              <div className="text-sm text-gray-600">Completeness</div>
            </div>
            <div className="text-center p-3 bg-red-50 rounded-lg">
              <div className="text-2xl font-bold text-red-600">{validation.missingCritical.length}</div>
              <div className="text-sm text-gray-600">Critical Missing</div>
            </div>
            <div className="text-center p-3 bg-amber-50 rounded-lg">
              <div className="text-2xl font-bold text-amber-600">{validation.flaggedInconsistent.length}</div>
              <div className="text-sm text-gray-600">Inconsistencies</div>
            </div>
          </div>

          {validation.readyForAnalysis ? (
            <Alert className="border-green-200 bg-green-50">
              <CheckCircle className="h-4 w-4 text-green-600" />
              <AlertDescription className="text-green-800">
                Evidence meets minimum requirements for AI analysis. You can proceed to generate root cause analysis.
              </AlertDescription>
            </Alert>
          ) : (
            <Alert className="border-amber-200 bg-amber-50">
              <AlertCircle className="h-4 w-4 text-amber-600" />
              <AlertDescription className="text-amber-800">
                Additional evidence needed before AI analysis can proceed. 
                {validation.missingCritical.length > 0 && ` ${validation.missingCritical.length} critical field(s) required.`}
              </AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>

      {/* Active AI Prompts */}
      {activePrompts.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MessageSquare className="h-5 w-5 text-blue-600" />
              AI Guidance & Prompts
              <Badge variant="outline">{activePrompts.length} active</Badge>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            {activePrompts.map((prompt) => (
              <div key={prompt.id} className={`p-4 rounded-lg border-2 ${getSeverityColor(prompt.severity)}`}>
                <div className="flex items-start gap-3">
                  {getSeverityIcon(prompt.severity)}
                  <div className="flex-1">
                    <div className="font-medium text-gray-900 mb-1">{prompt.message}</div>
                    <div className="text-sm text-gray-600 mb-2">{prompt.context}</div>
                    <div className="text-sm font-medium text-blue-700">{prompt.suggestedAction}</div>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => dismissPrompt(prompt.id)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    ‚úï
                  </Button>
                </div>
              </div>
            ))}
          </CardContent>
        </Card>
      )}

      {/* Help & Guidance */}
      {showGuidance && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <HelpCircle className="h-5 w-5 text-blue-600" />
              Evidence Collection Guidance
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 text-sm">
              <div className="flex items-start gap-2">
                <Target className="h-4 w-4 text-blue-500 mt-0.5" />
                <div>
                  <strong>Be Specific:</strong> Detailed descriptions help AI identify patterns and root causes more accurately.
                </div>
              </div>
              <div className="flex items-start gap-2">
                <FileText className="h-4 w-4 text-blue-500 mt-0.5" />
                <div>
                  <strong>Include Context:</strong> Operating conditions, recent changes, and maintenance history are crucial.
                </div>
              </div>
              <div className="flex items-start gap-2">
                <CheckCircle className="h-4 w-4 text-blue-500 mt-0.5" />
                <div>
                  <strong>Verify Accuracy:</strong> Double-check equipment tags, dates, and technical details before proceeding.
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      <div className="flex justify-between items-center">
        <Button
          variant="outline"
          onClick={() => setShowGuidance(!showGuidance)}
          className="flex items-center gap-2"
        >
          <HelpCircle className="h-4 w-4" />
          {showGuidance ? 'Hide' : 'Show'} Guidance
        </Button>

        {validation.readyForAnalysis && (
          <Badge variant="default" className="bg-green-600">
            Ready for AI Analysis
          </Badge>
        )}
      </div>
    </div>
  );
}

client/src/components/ai-assistant.tsx.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { 
  Bot, 
  MessageCircle, 
  Lightbulb, 
  CheckCircle, 
  ArrowRight,
  HelpCircle,
  Target,
  Sparkles,
  ChevronDown,
  ChevronUp
} from "lucide-react";

interface AIAssistantProps {
  currentQuestion: any;
  currentValue: any;
  evidenceData: any;
  onSuggestion: (value: string) => void;
  onFieldComplete: () => void;
}

interface AISuggestion {
  type: 'example' | 'context' | 'next_step' | 'validation' | 'improvement';
  message: string;
  value?: string;
  action?: string;
}

export default function AIAssistant({ 
  currentQuestion, 
  currentValue, 
  evidenceData,
  onSuggestion,
  onFieldComplete 
}: AIAssistantProps) {
  const [suggestions, setSuggestions] = useState<AISuggestion[]>([]);
  const [isExpanded, setIsExpanded] = useState(true);
  const [conversationMode, setConversationMode] = useState(false);
  const [isTyping, setIsTyping] = useState(false);

  useEffect(() => {
    if (currentQuestion) {
      generateIntelligentSuggestions();
    }
  }, [currentQuestion, currentValue, evidenceData]);

  const generateIntelligentSuggestions = () => {
    setIsTyping(true);

    // Simulate AI thinking delay
    setTimeout(() => {
      const newSuggestions: AISuggestion[] = [];

      // Context-aware suggestions based on question type and content
      generateContextualSuggestions(newSuggestions);

      // Equipment-specific guidance
      generateEquipmentSpecificGuidance(newSuggestions);

      // Value validation and improvement suggestions
      if (currentValue) {
        generateValueValidation(newSuggestions);
      }

      // Next steps suggestions
      generateNextStepSuggestions(newSuggestions);

      setSuggestions(newSuggestions);
      setIsTyping(false);
    }, 800);
  };

  const generateContextualSuggestions = (suggestions: AISuggestion[]) => {
    const questionId = currentQuestion.id;
    const equipmentType = evidenceData.equipment_type || '';

    switch (questionId) {
      case 'equipment_tag':
        suggestions.push({
          type: 'context',
          message: 'Equipment tags help us identify the exact asset and access maintenance history.',
        });
        if (!currentValue) {
          suggestions.push({
            type: 'example',
            message: 'Examples: "P-101", "PUMP-A", "WTR-PMP-001". Check the equipment nameplate or P&ID drawings.',
            value: equipmentType.includes('Pump') ? 'P-101' : 'EQ-001'
          });
        }
        break;

      case 'observed_problem':
        suggestions.push({
          type: 'context',
          message: 'Detailed problem descriptions help identify failure patterns and root causes more accurately.',
        });
        if (!currentValue || currentValue.length < 10) {
          suggestions.push({
            type: 'example',
            message: `For ${equipmentType || 'equipment'} failures, describe: What you saw, heard, or measured. When it started. How it progressed.`,
            value: equipmentType.includes('Pump') ? 'Pump began making grinding noise, then seized completely. Water leaking from mechanical seal.' : 'Equipment stopped operating normally. Unusual noise detected.'
          });
        }
        break;

      case 'event_datetime':
        suggestions.push({
          type: 'context',
          message: 'Precise timing helps correlate the failure with operating conditions and recent activities.',
        });
        if (!currentValue) {
          suggestions.push({
            type: 'example',
            message: 'Use the exact time when the problem was first noticed, not when it was reported.',
          });
        }
        break;

      case 'operating_mode':
        if (equipmentType.includes('Pump')) {
          suggestions.push({
            type: 'context',
            message: 'Pump failure modes vary significantly based on operating state. Starting failures often indicate electrical issues, while running failures suggest mechanical problems.',
          });
        }
        break;

      case 'last_maintenance_date':
        suggestions.push({
          type: 'context',
          message: 'Recent maintenance can reveal if the failure is related to installation errors, part quality, or maintenance procedures.',
        });
        if (evidenceData.observed_problem?.toLowerCase().includes('seal')) {
          suggestions.push({
            type: 'next_step',
            message: 'Since this appears to be a seal failure, the last seal maintenance date would be particularly valuable.',
          });
        }
        break;
    }
  };

  const generateEquipmentSpecificGuidance = (suggestions: AISuggestion[]) => {
    const equipmentType = evidenceData.equipment_type || '';
    const problemDescription = evidenceData.observed_problem || '';

    if (equipmentType.includes('Centrifugal Pump')) {
      if (problemDescription.toLowerCase().includes('vibration')) {
        suggestions.push({
          type: 'next_step',
          message: 'For pump vibration issues, consider collecting: bearing condition, alignment status, impeller condition, and suction conditions.',
        });
      }

      if (problemDescription.toLowerCase().includes('seal')) {
        suggestions.push({
          type: 'context',
          message: 'Pump seal failures are often caused by: dry running, excessive heat, contamination, or incorrect installation.',
        });
      }

      if (currentQuestion.id === 'operating_mode') {
        suggestions.push({
          type: 'context',
          message: 'For pumps: "Running" suggests mechanical failure, "Starting" indicates electrical/control issues, "Idle" may point to external damage.',
        });
      }
    }
  };

  const generateValueValidation = (suggestions: AISuggestion[]) => {
    const questionId = currentQuestion.id;
    const value = currentValue;

    switch (questionId) {
      case 'equipment_tag':
        if (typeof value === 'string' && value.length < 3) {
          suggestions.push({
            type: 'validation',
            message: 'Equipment tag seems short. Most plant tags are 4-10 characters. Please verify this is the complete tag.',
          });
        }
        if (typeof value === 'string' && !/[A-Z0-9-]/.test(value.toUpperCase())) {
          suggestions.push({
            type: 'validation',
            message: 'Equipment tags typically contain letters, numbers, and hyphens. Please check the format.',
          });
        }
        break;

      case 'observed_problem':
        if (typeof value === 'string') {
          if (value.length < 20) {
            suggestions.push({
              type: 'improvement',
              message: 'More detail would help. Consider adding: timeline, severity, any unusual sounds/smells, what happened just before.',
            });
          }
          if (value.toLowerCase().includes('failed') && !value.toLowerCase().includes('how')) {
            suggestions.push({
              type: 'improvement',
              message: 'Try to describe HOW it failed, not just that it failed. This helps identify the failure mode.',
            });
          }
        }
        break;

      case 'event_datetime':
        const eventDate = new Date(value);
        const now = new Date();
        const daysDiff = (now.getTime() - eventDate.getTime()) / (1000 * 3600 * 24);

        if (daysDiff > 7) {
          suggestions.push({
            type: 'validation',
            message: `Event was ${Math.round(daysDiff)} days ago. Older events may have less reliable evidence. Consider if any additional documentation exists from that time.`,
          });
        }
        if (eventDate > now) {
          suggestions.push({
            type: 'validation',
            message: 'Event date is in the future. Please check the date and time.',
          });
        }
        break;
    }
  };

  const generateNextStepSuggestions = (suggestions: AISuggestion[]) => {
    const completedFields = Object.keys(evidenceData).filter(key => 
      evidenceData[key] !== undefined && evidenceData[key] !== null && evidenceData[key] !== ''
    );

    // Suggest related fields to complete next
    if (currentQuestion.id === 'equipment_tag' && currentValue) {
      suggestions.push({
        type: 'next_step',
        message: 'Great! Next, I suggest completing the equipment location and then the maintenance history for this tag.',
      });
    }

    if (currentQuestion.id === 'observed_problem' && currentValue && evidenceData.event_datetime) {
      suggestions.push({
        type: 'next_step',
        message: 'Perfect! With the problem description and timing, now let\'s capture who detected it and the operating conditions.',
      });
    }

    // Progress encouragement
    if (completedFields.length > 5) {
      suggestions.push({
        type: 'next_step',
        message: `Excellent progress! You've completed ${completedFields.length} fields. The evidence is building a clear picture of the failure.`,
      });
    }
  };

  const applySuggestion = (suggestion: AISuggestion) => {
    if (suggestion.value) {
      onSuggestion(suggestion.value);
    }
  };

  const getSuggestionIcon = (type: string) => {
    switch (type) {
      case 'example': return <Lightbulb className="h-4 w-4 text-amber-500" />;
      case 'context': return <HelpCircle className="h-4 w-4 text-blue-500" />;
      case 'next_step': return <ArrowRight className="h-4 w-4 text-green-500" />;
      case 'validation': return <Target className="h-4 w-4 text-red-500" />;
      case 'improvement': return <Sparkles className="h-4 w-4 text-purple-500" />;
      default: return <MessageCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  const getSuggestionColor = (type: string) => {
    switch (type) {
      case 'example': return 'border-amber-200 bg-amber-50';
      case 'context': return 'border-blue-200 bg-blue-50';
      case 'next_step': return 'border-green-200 bg-green-50';
      case 'validation': return 'border-red-200 bg-red-50';
      case 'improvement': return 'border-purple-200 bg-purple-50';
      default: return 'border-gray-200 bg-gray-50';
    }
  };

  if (!currentQuestion) return null;

  return (
    <Card className="border-blue-200">
      <CardHeader 
        className="cursor-pointer" 
        onClick={() => setIsExpanded(!isExpanded)}
      >
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Bot className="h-5 w-5 text-blue-600" />
            AI Assistant
            {isTyping && <span className="text-sm text-blue-500">Thinking...</span>}
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="outline" className="text-xs">
              {suggestions.length} suggestions
            </Badge>
            {isExpanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
          </div>
        </CardTitle>
      </CardHeader>

      {isExpanded && (
        <CardContent className="space-y-4">
          {/* Current Field Context */}
          <div className="p-3 bg-blue-50 rounded-lg border border-blue-200">
            <div className="flex items-center gap-2 mb-2">
              <Target className="h-4 w-4 text-blue-600" />
              <span className="font-medium text-blue-900">Current Field: {currentQuestion.question}</span>
            </div>
            <p className="text-sm text-blue-700">
              {currentQuestion.required ? 'This field is required for analysis.' : 'This field provides additional context.'}
              {currentQuestion.context && ` ${currentQuestion.context}`}
            </p>
          </div>

          {/* AI Suggestions */}
          {suggestions.length > 0 && (
            <div className="space-y-3">
              {suggestions.map((suggestion, index) => (
                <div 
                  key={index} 
                  className={`p-3 rounded-lg border-2 ${getSuggestionColor(suggestion.type)}`}
                >
                  <div className="flex items-start gap-3">
                    {getSuggestionIcon(suggestion.type)}
                    <div className="flex-1">
                      <div className="text-sm text-gray-800 mb-2">{suggestion.message}</div>
                      {suggestion.value && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => applySuggestion(suggestion)}
                          className="text-xs"
                        >
                          Apply Suggestion
                        </Button>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Quick Actions */}
          <div className="flex flex-wrap gap-2 pt-2 border-t">
            <Button
              variant="outline"
              size="sm"
              onClick={() => generateIntelligentSuggestions()}
              className="flex items-center gap-1"
            >
              <Sparkles className="h-3 w-3" />
              More Help
            </Button>

            <Button
              variant="outline"
              size="sm"
              onClick={() => setConversationMode(!conversationMode)}
              className="flex items-center gap-1"
            >
              <MessageCircle className="h-3 w-3" />
              {conversationMode ? 'Exit Chat' : 'Chat Mode'}
            </Button>

            {currentValue && (
              <Button
                variant="outline"
                size="sm"
                onClick={onFieldComplete}
                className="flex items-center gap-1 text-green-600"
              >
                <CheckCircle className="h-3 w-3" />
                Looks Good
              </Button>
            )}
          </div>

          {/* Conversational Mode */}
          {conversationMode && (
            <div className="p-3 bg-gray-50 rounded-lg border">
              <div className="text-sm text-gray-600 mb-2">Ask me anything about this investigation:</div>
              <div className="flex gap-2">
                <Input placeholder="e.g., What maintenance records should I look for?" className="text-sm" />
                <Button size="sm">Ask</Button>
              </div>
            </div>
          )}
        </CardContent>
      )}
    </Card>
  );
}

