import { useState, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  CheckCircle, 
  AlertCircle, 
  Lightbulb, 
  Eye,
  EyeOff,
  Sparkles 
} from "lucide-react";

interface SmartFieldWrapperProps {
  question: any;
  value: any;
  children: React.ReactNode;
  onValueChange: (value: any) => void;
  evidenceData: any;
}

interface FieldInsight {
  type: 'success' | 'warning' | 'suggestion' | 'context';
  message: string;
  action?: () => void;
}

export default function SmartFieldWrapper({ 
  question, 
  value, 
  children, 
  onValueChange,
  evidenceData 
}: SmartFieldWrapperProps) {
  const [insights, setInsights] = useState<FieldInsight[]>([]);
  const [showInsights, setShowInsights] = useState(false);
  const [isComplete, setIsComplete] = useState(false);
  const [hasInteracted, setHasInteracted] = useState(false);

  useEffect(() => {
    if (hasInteracted) {
      analyzeField();
    }
  }, [value, evidenceData, hasInteracted]);

  const analyzeField = () => {
    const newInsights: FieldInsight[] = [];

    // Check completeness
    const hasValue = value !== undefined && value !== null && value !== '';
    setIsComplete(hasValue && (!question.required || validateFieldContent()));

    // Generate contextual insights
    generateContextualInsights(newInsights);

    // Validate content quality
    if (hasValue) {
      validateContentQuality(newInsights);
    } else if (question.required) {
      newInsights.push({
        type: 'warning',
        message: `${question.question} is required for analysis.`
      });
    }

    // Cross-field logic insights
    generateCrossFieldInsights(newInsights);

    setInsights(newInsights);
    setShowInsights(newInsights.length > 0);
  };

  const generateContextualInsights = (insights: FieldInsight[]) => {
    const questionId = question.id;
    const equipmentType = evidenceData.equipment_type || '';

    // Smart contextual help based on question and equipment
    const contextMap: Record<string, string> = {
      'equipment_tag': 'Equipment tags link to maintenance history and specifications. Check nameplates or P&IDs for accuracy.',
      'observed_problem': 'Detailed descriptions help identify failure patterns. Include what you saw, heard, and timeline.',
      'event_datetime': 'Precise timing correlates failure with operations. Use when first detected, not reported.',
      'operating_mode': equipmentType.includes('Pump') 
        ? 'Pump failures vary by state: Running=mechanical, Starting=electrical, Idle=external damage'
        : 'Operating mode affects failure analysis and probable causes.',
      'last_maintenance_date': 'Recent maintenance may indicate installation issues, part quality, or procedure problems.'
    };

    if (contextMap[questionId]) {
      insights.push({
        type: 'context',
        message: contextMap[questionId]
      });
    }
  };

  const validateContentQuality = (insights: FieldInsight[]) => {
    const questionId = question.id;

    switch (questionId) {
      case 'equipment_tag':
        if (typeof value === 'string') {
          if (value.length < 3) {
            insights.push({
              type: 'warning',
              message: 'Tag seems short. Typical plant tags are 4-10 characters.'
            });
          } else if (value.length >= 3) {
            insights.push({
              type: 'success',
              message: 'Good! Equipment tag format looks valid.'
            });
          }
        }
        break;

      case 'observed_problem':
        if (typeof value === 'string') {
          if (value.length < 15) {
            insights.push({
              type: 'suggestion',
              message: 'More detail would strengthen the analysis. Consider adding timeline, severity, sounds, or what happened just before.'
            });
          } else if (value.length >= 30) {
            insights.push({
              type: 'success',
              message: 'Excellent detail! This will help identify the failure mode.'
            });
          }
        }
        break;

      case 'event_datetime':
        const eventDate = new Date(value);
        const now = new Date();
        const daysDiff = (now.getTime() - eventDate.getTime()) / (1000 * 3600 * 24);

        if (daysDiff < 0) {
          insights.push({
            type: 'warning',
            message: 'Date appears to be in the future. Please verify.'
          });
        } else if (daysDiff > 30) {
          insights.push({
            type: 'warning',
            message: `Event was ${Math.round(daysDiff)} days ago. Evidence quality may be reduced.`
          });
        } else {
          insights.push({
            type: 'success',
            message: 'Timeline looks good for evidence reliability.'
          });
        }
        break;
    }
  };

  const generateCrossFieldInsights = (insights: FieldInsight[]) => {
    // Smart cross-field analysis
    if (question.id === 'last_maintenance_date' && evidenceData.observed_problem) {
      const problem = evidenceData.observed_problem.toLowerCase();
      if (problem.includes('seal') || problem.includes('leak')) {
        insights.push({
          type: 'suggestion',
          message: 'Since this appears to be a seal issue, the last seal maintenance date would be particularly valuable.'
        });
      }
    }

    // Equipment-specific cross-field logic
    if (question.id === 'operating_mode' && evidenceData.equipment_type?.includes('Pump')) {
      if (evidenceData.observed_problem?.toLowerCase().includes('vibration')) {
        insights.push({
          type: 'context',
          message: 'For pump vibration during operation, consider alignment, bearing condition, and cavitation.'
        });
      }
    }
  };

  const validateFieldContent = (): boolean => {
    if (!value) return false;

    // Basic validation rules
    switch (question.type) {
      case 'text':
        return typeof value === 'string' && value.trim().length > 0;
      case 'number':
        return typeof value === 'number' || !isNaN(Number(value));
      case 'date':
      case 'datetime':
        return !isNaN(new Date(value).getTime());
      case 'boolean':
        return typeof value === 'boolean';
      case 'select':
        return question.options?.includes(value);
      default:
        return true;
    }
  };

  const getInsightIcon = (type: string) => {
    switch (type) {
      case 'success': return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'warning': return <AlertCircle className="h-4 w-4 text-amber-500" />;
      case 'suggestion': return <Lightbulb className="h-4 w-4 text-blue-500" />;
      case 'context': return <Sparkles className="h-4 w-4 text-purple-500" />;
      default: return <AlertCircle className="h-4 w-4 text-gray-500" />;
    }
  };

  const getInsightColor = (type: string) => {
    switch (type) {
      case 'success': return 'border-green-200 bg-green-50 text-green-800';
      case 'warning': return 'border-amber-200 bg-amber-50 text-amber-800';
      case 'suggestion': return 'border-blue-200 bg-blue-50 text-blue-800';
      case 'context': return 'border-purple-200 bg-purple-50 text-purple-800';
      default: return 'border-gray-200 bg-gray-50 text-gray-800';
    }
  };

  return (
    <div 
      className="space-y-3"
      onFocus={() => setHasInteracted(true)}
      onClick={() => setHasInteracted(true)}
    >
      <div className="relative">
        {children}

        {/* Field Status Indicator */}
        {hasInteracted && (
          <div className="absolute -right-1 -top-1">
            {isComplete ? (
              <CheckCircle className="h-5 w-5 text-green-500 bg-white rounded-full" />
            ) : question.required ? (
              <AlertCircle className="h-5 w-5 text-amber-500 bg-white rounded-full" />
            ) : null}
          </div>
        )}
      </div>

      {/* Real-time Insights */}
      {hasInteracted && insights.length > 0 && (
        <Card className="border-dashed">
          <CardContent className="p-3">
            <div className="flex items-center justify-between mb-2">
              <div className="flex items-center gap-2">
                <Sparkles className="h-4 w-4 text-blue-500" />
                <span className="text-sm font-medium">AI Insights</span>
                <Badge variant="outline" className="text-xs">
                  {insights.length}
                </Badge>
              </div>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowInsights(!showInsights)}
              >
                {showInsights ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
              </Button>
            </div>

            {showInsights && (
              <div className="space-y-2">
                {insights.map((insight, index) => (
                  <div 
                    key={index}
                    className={`flex items-start gap-2 p-2 rounded-md text-xs ${getInsightColor(insight.type)}`}
                  >
                    {getInsightIcon(insight.type)}
                    <span className="flex-1">{insight.message}</span>
                    {insight.action && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={insight.action}
                        className="text-xs h-6"
                      >
                        Fix
                      </Button>
                    )}
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/intelligent-ai-ass.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Brain, 
  CheckCircle, 
  AlertTriangle, 
  TrendingUp, 
  Camera, 
  FileText,
  Lightbulb,
  Target,
  MessageCircle
} from "lucide-react";

interface IntelligentAIAssistantProps {
  equipmentType: string;
  currentQuestion: any;
  currentValue: any;
  evidenceData: Record<string, any>;
  onSuggestion: (suggestion: string) => void;
}

export default function IntelligentAIAssistant({ 
  equipmentType, 
  currentQuestion, 
  currentValue, 
  evidenceData,
  onSuggestion 
}: IntelligentAIAssistantProps) {
  const [assistantState, setAssistantState] = useState({
    activePrompts: [] as string[],
    missingEvidence: [] as string[],
    smartSuggestions: [] as string[],
    contextualHelp: "",
    validationResults: null as any
  });

  // Fetch equipment-specific AI prompts
  const { data: promptData } = useQuery({
    queryKey: ['/api/evidence-library/equipment', equipmentType, 'prompts', currentQuestion?.type],
    queryFn: () => fetch(`/api/evidence-library/equipment/${equipmentType}/prompts/${currentQuestion?.type}`)
      .then(res => res.json()),
    enabled: !!equipmentType && !!currentQuestion?.type,
  });

  // Fetch equipment requirements and validate evidence
  const { data: requirementsData } = useQuery({
    queryKey: ['/api/evidence-library/equipment', equipmentType, 'requirements'],
    queryFn: () => fetch(`/api/evidence-library/equipment/${equipmentType}/requirements`)
      .then(res => res.json()),
    enabled: !!equipmentType,
  });

  // Real-time evidence validation
  const { data: validationData } = useQuery({
    queryKey: ['/api/evidence-library/validate-evidence', equipmentType, evidenceData],
    queryFn: () => fetch('/api/evidence-library/validate-evidence', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        equipmentType, 
        evidenceData,
        symptoms: extractSymptoms(evidenceData)
      }),
    }).then(res => res.json()),
    enabled: !!equipmentType && Object.keys(evidenceData).length > 0,
  });

  useEffect(() => {
    if (promptData?.data?.prompt) {
      generateContextualHelp();
    }
    if (requirementsData?.data) {
      checkMissingEvidence();
    }
    if (validationData?.data?.validation) {
      setAssistantState(prev => ({
        ...prev,
        validationResults: validationData.data.validation
      }));
    }
  }, [promptData, requirementsData, validationData, currentQuestion, currentValue]);

  const extractSymptoms = (data: Record<string, any>): string[] => {
    const symptoms = [];
    if (data.observed_problem) symptoms.push(data.observed_problem);
    if (data.symptom_description) symptoms.push(data.symptom_description);
    return symptoms;
  };

  const generateContextualHelp = () => {
    if (!promptData?.data?.prompt || !currentQuestion) return;

    const prompt = promptData.data.prompt;
    let helpText = prompt.context;

    // UNIVERSAL GUIDANCE: Use Evidence Library data for equipment-specific help
    // NO HARDCODED EQUIPMENT PROMPTS! All assistance from Evidence Library intelligence
    // Guidance now generated dynamically from Evidence Library 'aiOrInvestigatorQuestions' field
    if (equipmentType && currentQuestion) {
      helpText = "I'll help you provide detailed, specific information for your investigation. Please include measurements, timelines, and observed conditions.";
    }

    setAssistantState(prev => ({
      ...prev,
      contextualHelp: helpText
    }));
  };

  const checkMissingEvidence = () => {
    if (!requirementsData?.data) return;

    const missing = [];
    const { requiredTrends, requiredAttachments } = requirementsData.data;

    // Check missing mandatory trends
    requiredTrends?.forEach((trend: any) => {
      if (trend.mandatory && !evidenceData[trend.id]) {
        missing.push(`üìä ${trend.name}: ${trend.description}`);
      }
    });

    // Check missing mandatory attachments
    requiredAttachments?.forEach((attachment: any) => {
      if (attachment.mandatory && !evidenceData[attachment.id]) {
        missing.push(`üìé ${attachment.name}: ${attachment.description}`);
      }
    });

    setAssistantState(prev => ({
      ...prev,
      missingEvidence: missing
    }));
  };

  const generateSmartSuggestions = () => {
    const suggestions = [];

    // Equipment-specific intelligent suggestions
    // UNIVERSAL SUGGESTIONS: Use Evidence Library for equipment guidance
    // NO HARDCODED EQUIPMENT CHECKS! All suggestions from Evidence Library intelligence
    if (equipmentType && currentValue?.includes('vibration')) {
      suggestions.push("Vibration analysis requires: overall levels, frequency spectrum, trending data");
    }
    if (currentValue?.includes('leak')) {
      suggestions.push("Leak details needed: rate, location, fluid type, timeline");
    }

    return suggestions;
  };

  const challengeVagueResponse = () => {
    if (!currentValue || typeof currentValue !== 'string') return null;

    const vague = currentValue.toLowerCase();

    // Challenge generic responses
    if (vague.includes('fine') || vague.includes('normal') || vague.includes('ok')) {
      return "‚ö†Ô∏è **Generic Response Detected** - If everything was 'fine', how do you explain the current failure? Provide specific measurements and observations.";
    }

    if (vague.includes('high') && !vague.match(/\d/)) {
      return "üìä **Need Quantified Data** - 'High' is relative. Provide actual measurements with units (e.g., '8.5 mm/s vibration, normal 2.1 mm/s').";
    }

    if (vague.includes('leak') && vague.length < 50) {
      return "üîç **Leak Details Required** - Specify: leak rate, fluid type, exact location, visual appearance, and timeline of development.";
    }

    return null;
  };

  const smartSuggestions = generateSmartSuggestions();
  const vagueChallengeCheck = challengeVagueResponse();

  if (!equipmentType) return null;

  return (
    <div className="space-y-4">
      {/* AI Assistant Header */}
      <Card className="border-blue-200 bg-blue-50">
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center gap-2 text-blue-800">
            <Brain className="h-5 w-5" />
            Equipment-Specific AI Assistant
            <Badge variant="outline" className="bg-white text-blue-700">
              {equipmentType}
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          {/* Contextual Help */}
          {assistantState.contextualHelp && (
            <Alert className="border-blue-200 bg-blue-50">
              <Lightbulb className="h-4 w-4" />
              <AlertDescription className="whitespace-pre-line text-sm">
                {assistantState.contextualHelp}
              </AlertDescription>
            </Alert>
          )}

          {/* Challenge Vague Responses */}
          {vagueChallengeCheck && (
            <Alert className="border-amber-200 bg-amber-50">
              <AlertTriangle className="h-4 w-4 text-amber-600" />
              <AlertDescription className="text-amber-800">
                {vagueChallengeCheck}
              </AlertDescription>
            </Alert>
          )}

          {/* Smart Suggestions */}
          {smartSuggestions.length > 0 && (
            <div className="space-y-2">
              <h4 className="text-sm font-medium text-blue-800 flex items-center gap-2">
                <Target className="h-4 w-4" />
                Smart Suggestions
              </h4>
              {smartSuggestions.map((suggestion, index) => (
                <Alert key={index} className="border-green-200 bg-green-50">
                  <CheckCircle className="h-4 w-4 text-green-600" />
                  <AlertDescription className="text-green-800 text-sm">
                    {suggestion}
                  </AlertDescription>
                </Alert>
              ))}
            </div>
          )}

          {/* Evidence Validation Summary */}
          {assistantState.validationResults && (
            <div className="space-y-2">
              <h4 className="text-sm font-medium text-blue-800 flex items-center gap-2">
                <CheckCircle className="h-4 w-4" />
                Evidence Completeness: {assistantState.validationResults.completeness}%
              </h4>

              {assistantState.validationResults.failureMode && (
                <Alert className="border-purple-200 bg-purple-50">
                  <MessageCircle className="h-4 w-4 text-purple-600" />
                  <AlertDescription className="text-purple-800">
                    <strong>Likely Failure Mode:</strong> {assistantState.validationResults.failureMode.name}
                    <br />
                    <span className="text-sm">{assistantState.validationResults.failureMode.description}</span>
                  </AlertDescription>
                </Alert>
              )}
            </div>
          )}

          {/* Missing Critical Evidence */}
          {assistantState.missingEvidence.length > 0 && (
            <div className="space-y-2">
              <h4 className="text-sm font-medium text-red-800 flex items-center gap-2">
                <AlertTriangle className="h-4 w-4" />
                Missing Critical Evidence
              </h4>
              {assistantState.missingEvidence.slice(0, 3).map((missing, index) => (
                <Alert key={index} className="border-red-200 bg-red-50">
                  <AlertTriangle className="h-4 w-4 text-red-600" />
                  <AlertDescription className="text-red-800 text-sm">
                    {missing}
                  </AlertDescription>
                </Alert>
              ))}
              {assistantState.missingEvidence.length > 3 && (
                <p className="text-sm text-gray-600">
                  +{assistantState.missingEvidence.length - 3} more required items...
                </p>
              )}
            </div>
          )}

          {/* Equipment-Specific Examples */}
          {promptData?.data?.prompt?.examples && currentQuestion && (
            <div className="space-y-2">
              <h4 className="text-sm font-medium text-blue-800 flex items-center gap-2">
                <FileText className="h-4 w-4" />
                Good Examples for {currentQuestion.label}
              </h4>
              {promptData.data.prompt.examples.slice(0, 2).map((example: string, index: number) => (
                <div key={index} className="bg-gray-50 p-3 rounded text-sm border-l-4 border-blue-400">
                  <strong>Example {index + 1}:</strong> "{example}"
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

shared/equipment-evidence-library.ts.content:
// Equipment-specific evidence requirements and prompts library
export interface EvidenceRequirement {
  id: string;
  label: string;
  type: 'trend_data' | 'measurement' | 'visual' | 'log' | 'text';
  required: boolean;
  prompt: string;
  validation?: string;
  units?: string;
  acceptedFormats?: string[];
}

export interface EquipmentEvidenceConfig {
  equipmentType: string;
  requiredTrendData: string[];
  criticalEvidence: EvidenceRequirement[];
  typicalSymptoms: string[];
  failurePatterns: { pattern: string; indicators: string[]; requiredEvidence: string[] }[];
  smartPrompts: { condition: string; prompt: string; action?: string }[];
}

export const EQUIPMENT_EVIDENCE_LIBRARY: Record<string, EquipmentEvidenceConfig> = {
  'Centrifugal Pump': {
    equipmentType: 'Centrifugal Pump',
    requiredTrendData: ['vibration', 'suction_pressure', 'discharge_pressure', 'flow_rate', 'bearing_temperature'],
    criticalEvidence: [
      {
        id: 'vibration_trend',
        label: 'Vibration Trend Data',
        type: 'trend_data',
        required: true,
        prompt: 'Upload vibration trend showing 1X, 2X, 3X running speed components. Look for: bearing frequencies (BPFI, BPFO), imbalance (1X), misalignment (2X), looseness (multiple frequencies).',
        validation: 'Vibration trend is essential for pump diagnosis. Without it, we cannot identify bearing wear, misalignment, or mechanical issues.',
        acceptedFormats: ['csv', 'xlsx', 'png', 'jpg', 'pdf']
      },
      {
        id: 'pressure_trend',
        label: 'Suction & Discharge Pressure Trend',
        type: 'trend_data',
        required: true,
        prompt: 'Provide pressure trends for both suction and discharge. Note any pressure drops, spikes, or oscillations. Include NPSH calculations if available.',
        validation: 'Pressure data reveals cavitation, blockages, or system issues that cause pump failures.',
        acceptedFormats: ['csv', 'xlsx', 'png', 'jpg']
      },
      {
        id: 'seal_inspection',
        label: 'Seal Physical Condition',
        type: 'visual',
        required: true,
        prompt: 'Document seal faces: any scoring (depth in Œºm), discoloration, carbon dust, spring condition, O-ring swelling. Was seal OEM or aftermarket? Installation torque verified?',
        validation: 'Seal condition directly indicates failure mode and root cause.',
        acceptedFormats: ['jpg', 'png', 'pdf']
      },
      {
        id: 'bearing_condition',
        label: 'Bearing Assessment',
        type: 'measurement',
        required: true,
        prompt: 'Provide bearing temperature readings, vibration at bearing locations, grease condition analysis. Any metallic particles? Discoloration? Running clearances?',
        validation: 'Bearing condition is critical for mechanical failure analysis.',
        units: '¬∞C, mm/s'
      },
      {
        id: 'alignment_data',
        label: 'Alignment Measurements',
        type: 'measurement',
        required: false,
        prompt: 'Last alignment check date and dial indicator readings (angular and parallel). Foundation condition, coupling wear, soft foot measurements.',
        units: 'mm, mils'
      }
    ],
    typicalSymptoms: ['seal leakage', 'high vibration', 'bearing noise', 'temperature rise', 'flow loss', 'pressure drop'],
    failurePatterns: [
      {
        pattern: 'Seal Failure',
        indicators: ['leakage', 'seal wear', 'contamination'],
        requiredEvidence: ['seal_inspection', 'operating_conditions', 'maintenance_history']
      },
      {
        pattern: 'Bearing Failure',
        indicators: ['vibration increase', 'temperature rise', 'noise'],
        requiredEvidence: ['vibration_trend', 'bearing_condition', 'lubrication_analysis']
      },
      {
        pattern: 'Cavitation',
        indicators: ['pressure drop', 'noise', 'erosion'],
        requiredEvidence: ['pressure_trend', 'npsh_calculation', 'impeller_inspection']
