                          value={field.value}
                        >
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select equipment group" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {equipmentGroups.map((group: string) => (
                              <SelectItem key={group} value={group}>
                                {group}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* LEVEL 2: Equipment Type */}
                  <FormField
                    control={form.control}
                    name="equipmentType"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Equipment Type (Level 2)</FormLabel>
                        <Select 
                          onValueChange={(value) => {
                            field.onChange(value);
                            // Reset subtype when type changes
                            form.setValue("equipmentSubtype", "");
                          }} 
                          value={field.value}
                          disabled={!selectedEquipmentGroup}
                        >
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder={
                                !selectedEquipmentGroup 
                                  ? "Select equipment group first" 
                                  : equipmentTypes.length === 0 
                                  ? "Loading types..." 
                                  : "Select equipment type"
                              } />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {equipmentTypes.map((type: string) => (
                              <SelectItem key={type} value={type}>
                                {type}
                              </SelectItem>
                            ))}
